import React from 'react'
import { createBrowserRouter, RouterProvider } from 'react-router-dom';
import Layout from "./Compontent/Layout/Layout";
import Logout from "./Compontent/Logout/Logout";
import Home from "../src/Compontent/Home/Home";
import Create from "./Compontent/Create/Create";
import Settings from "./Compontent/Settings/Settings";
import Profile from './Compontent/Profile/Profile';
//import NotFound from './Compontent/NoFound/NoFound';
import IconButton from '@mui/material/IconButton';
import Box from '@mui/material/Box';
import { useTheme, ThemeProvider, createTheme } from '@mui/material/styles';
import Brightness4Icon from '@mui/icons-material/Brightness4';
import Brightness7Icon from '@mui/icons-material/Brightness7';
import { grey } from '@mui/material/colors';

const ColorModeContext = React.createContext({ toggleColorMode: () => {} });




 function App() {


  const theme = useTheme();
  const colorMode = React.useContext(ColorModeContext);
  return (
    <Box
      sx={{
        display: 'flex',
        width: '100%',
        alignItems: 'center',
        justifyContent: 'center',
        bgcolor: 'background.default',
        color: 'text.primary',
        borderRadius: 1,
        p: 3,
        values: {
          mobile: 0,
          tablet: 640,
          laptop: 1024,
          desktop: 1200,
        },

      }}
    >
      {theme.palette.mode} mode

      <IconButton sx={{  ml:{md:"180" ,} ,pt:5 , mr:{xs:"100px"} }} onClick={colorMode.toggleColorMode} color="inherit" >
        {theme.palette.mode === 'dark' ? <Brightness7Icon sx={{ color:"orange"}} /> : <Brightness4Icon />}
      </IconButton>
    </Box>
  );
}
export default function ToggleColorMode() {
  const [mode, setMode] = React.useState(localStorage.getItem("currentMode"));
  const colorMode = React.useMemo(
    () => ({
      toggleColorMode: () => {
        localStorage.setItem("currentMode" ,theme.palette.mode=== 'dark' ? 'light' : 'dark')
        setMode((prevMode) => (prevMode === 'light' ? 'dark' : 'light'));
      },
    }),
    [],
  );

  const theme = React.useMemo(
    () =>
      createTheme({
        palette: {
          mode,
          ...(mode === 'light'
          ? {
              ali:{
                main: '#3f50b5',
              },
              roma:{
                main: grey[400]
              }
            }
          : {
              // palette values for dark mode
              ali:{
                main: "teal",
              },
              roma:{
                main:grey[700]

      }
            }),
        },
      }),
    [mode],
  );

  let routes = createBrowserRouter([{
    path: '/', element: <Layout />,
    children: [
      { index: true, element: <Home /> },
      { path: '*', element: <Home /> },

      { path: 'create', element: <Create /> },
      { path: 'profile', element: <Profile />},
      { path: 'settings', element: <Settings /> },
      { path: 'logout', element: <Logout /> },
    //  {path:'/' ,element:<Home/>},

    ]
  }])

  return (
    <>
        <ColorModeContext.Provider value={colorMode}>

      <ThemeProvider theme={theme}>
        <App />
        <RouterProvider router={routes} />

      </ThemeProvider>
    </ColorModeContext.Provider>


    </>
  );
}
